def freqdist1(token_text):
	tagged_sent = nltk.pos_tag(token_text)
	verb = []
	noun = []
	adjec = []

	for word,pos in tagged_sent:
		if pos in ['NN','NNP']:
			noun.append(word)
		elif pos in ['VB','VBD','VBG','VBN']:
			verb.append(word)
		elif pos in ['JJ','JJR','JJS']:
			adjec.append(word)

	freq1 = nltk.FreqDist(noun)
	array1 = freq1.most_common(20)
	#This is top 20 words in nouns

	freq2 = nltk.FreqDist(verb)
	array2 = freq2.most_common(20)
	#This is top 20 words in verb

	freq3 = nltk.FreqDist(adjec)
	array3 = freq3.most_common(20)
	#This is top 20 words in adjectives	

	return (array1,array2,array3)

